var Blackfeather = require('../blackfeather-2.0.0');

var client = new Blackfeather.Security.Cryptology.KeyExchange().Mix();
var serverPublic = "";

var KeyPair = new Blackfeather.Security.Cryptology.KeyExchange().KeyPair;
var clientHandshake = new KeyPair(client.Private, serverPublic);
var secret = new Blackfeather.Security.Cryptology.KeyExchange().Remix(clientHandshake);

console.log(`*** Client Private Key ***\n${client.Private}`);
console.log(`*** Client Public Key ***\n${client.Public}`);
console.log(`*** Secret Key ***\n${secret}`);